package no.nav.lydia

import com.auth0.jwk.JwkProviderBuilder
import io.ktor.application.*
import io.ktor.auth.*
import io.ktor.auth.jwt.*
import io.ktor.features.*
import io.ktor.metrics.micrometer.*
import io.ktor.routing.*
import io.ktor.serialization.*
import io.ktor.server.engine.*
import io.ktor.server.netty.*
import no.nav.lydia.appstatus.Metrics
import no.nav.lydia.appstatus.healthChecks
import no.nav.lydia.appstatus.metrics
import no.nav.lydia.sykefraversstatistikk.api.sykefraversstatistikk
import java.util.concurrent.TimeUnit


fun main() {
    val naisEnv = NaisEnvironment()
    val dataSource = createDataSource(naisEnv.database)
    runMigration(dataSource)

    embeddedServer(Netty, port = 8080) {
        lydiaBackend(naisEnv)
    }.also {
        // https://doc.nais.io/nais-application/good-practices/#handles-termination-gracefully
        it.addShutdownHook {
            it.stop(3, 5, TimeUnit.SECONDS)
        }
    }.start(wait = true)
}

fun Application.lydiaBackend(naisEnv: NaisEnvironment = NaisEnvironment()) {
    install(ContentNegotiation) {
        json()
    }
    install(IgnoreTrailingSlash)

    install(MicrometerMetrics) {
        registry = Metrics.appMicrometerRegistry
    }

    val jwkProvider = JwkProviderBuilder(naisEnv.security.azureConfig.jwksUri)
        .cached(10, 24, TimeUnit.HOURS)
        .rateLimited(10, 1, TimeUnit.MINUTES)
        .build()
    install(Authentication) {
        jwt {
            verifier(jwkProvider, naisEnv.security.azureConfig.issuer)
            validate { credentials ->
                try {
                    requireNotNull(credentials.payload.audience) {
                        "Auth: Missing audience in token"
                    }
                    log.info("Manual retreive of audience: ${credentials.payload.getClaim("aud")}")
                    log.info("Manual retreive of azp: ${credentials.payload.getClaim("azp")}")
                    log.info("Manual retreive of iss: ${credentials.payload.getClaim("iss")}")
                    credentials.payload.audience.forEach { aud ->
                        log.info("Audience received: $aud")
                    }
                    log.info("Audience expected: ${naisEnv.security.azureConfig.audience}")
                    require(credentials.payload.audience.contains(naisEnv.security.azureConfig.audience)) {
                        "Auth: Valid audience not found in claims"
                    }
                    JWTPrincipal(credentials.payload)
                } catch (e: Throwable) {
                    log.error("Feil under autentisering")
                    log.error(e.message)
                    null
                }
            }
        }
    }
    routing {
        healthChecks()
        metrics()
        authenticate {
            sykefraversstatistikk()
        }
    }

}